import React from "react";
import styled from "styled-components";

// types.ts
interface ImageProps {
  imageUrl: string | undefined;
  size: string;
  name?: string;
}

// inage: strig, size: sting
export default function ImageAI({ imageUrl, size, name }: ImageProps) {
    return (
    <Container>
      <Image className={name} imageUrl={imageUrl} size={size} />
      <ButtonContainer>
        <Button type="submit">확인</Button>
      </ButtonContainer>
    </Container>
  );
}

const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-top : 10%;
    width: 100%;
`

const Image = styled.div<ImageProps>`
  width: ${(props: ImageProps) => props.size};
  height: ${(props: ImageProps) => props.size};
  background-image: url(${(props: ImageProps) => `${props.imageUrl}`});
  background-size: contain;
  background-repeat : none;
  &.img {
    z-index: 1;
    position: relative;
    top: 110px;
    left: 60px;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 1.5rem;
`;

const Button = styled.button`
  border: none;
  box-shadow: rgba(97, 88, 88, 0.25) 0px 4px 1px;
  background-color: #dbd4c3;
  height: 50px;
  color: #5c5649;
  font-weight: bold;
`;
