import React, { useEffect, useState } from "react";
import styled from "styled-components";
import sample from "../../Assets/Images/sample.png";
import Book from "../../Components/Contents/Book";

// 컴포넌트 정의
export default function ReadingBooks() {

  return (
    <Container>
      <Title>나혜승님이 읽고 있는 책</Title>
      <List>
        <Book book={{
          imageUrl: sample,
          title: "불편한 편의점",
          categories: [],
          company: "나무 옆 의자"
        }} />
        <></>
      </List>
    </Container>
  );
}

// Styled Components
const Container = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;
    margin: 5% auto 10% auto;
  `
const Title = styled.div`
    font-size: 16px;
    font-weight: bold;
    color: #5c5649;
    width: 90%;
    margin: 5% auto;
  `;

const List = styled.div`
    width: 100%;
    display: flex;
    overflow-x: scroll;
    &::-webkit-scrollbar {
      display: none;
    }
  `;
