import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { LibraryBook } from '../../Store/Types'
import { useInView } from "react-intersection-observer/useInView";
// import Components

// Swiper
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/effect-coverflow";
import { EffectCoverflow } from "swiper";
import BookCover from "../../Components/Contents/BookCover";
import { getLibraryBooksAPI } from "../../apis/region";



// Component
export default function LibraryBooks() {
  const [books, setBooks] = useState<LibraryBook[]>();

  useEffect(() => {
    // 현재 지역 도서관 인기도서 불러오기
    // getLibraryBooks()
  }, [])

  const getLibraryBooks = async () => {
    const data = await getLibraryBooksAPI()

    let tmp: LibraryBook[] = []
    data.forEach((book: any) => {
      tmp.push({
        imageUrl: book.bookImageURL,
        ISBN: book.isbn13,
        ranking: book.ranking,
      })
    })
    setBooks(tmp)
  }

  return (
    <Container>
      <Title ref={ref} className={inView ? 'title' : ''}>
        유나님이 계신 지역의
        <br />
        인기 대출 도서
      </Title>
      <Desc>{ } 지역의 인기 도서를 만나보세요!</Desc>
      <SwiperWrap>
        <Swiper
          effect={"coverflow"}
          grabCursor={true}
          centeredSlides={true}
          slidesPerView={2}
          coverflowEffect={{
            rotate: 50,
            stretch: 0,
            depth: 100,
            modifier: 1,
          }}
          modules={[EffectCoverflow]}
        >
          <>{

            books?.map((book, idx) => {
              return (
                <SwiperSlide key={book.ISBN} style={{ display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }}>
                  <BookCover imageUrl={book.imageUrl} size={170} />
                  <Ranking>
                    {idx + 1}위
                  </Ranking>
                </SwiperSlide>
              )
            })
          }
          </>
        </Swiper>
      </SwiperWrap>
    </Container>
  );
};


// Styled Components
const Container = styled.div`
  width : 100%;
  height: 600px;
  background-color: #F9F9F7;
  display: flex;
  flex-direction: column;
  justify-content: center;
`

const Title = styled.div`
  font-size: 25px;
  font-weight: 700;
  text-align: center;
  margin: 3% 0; 
  text-shadow: 0px 3px 3px #00000040;
  &.title {
    animation: fadeIn 2s ease-in-out;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity:3;
      transform: none;
    }   
  }
`;

const Desc = styled.div`
  margin-top: 7%;
  font-size: 13px;
  font-weight: 500;
  color: #5c5c5c;
  text-align: center;
`;

const SwiperWrap = styled.div`
  margin-top: 12%;
`

const Ranking = styled.div`
  border: 2px solid #edc200;
  color: #edc200;
  border-radius: 20px;
  display: flex;
  width: 50px;
  margin: auto;
  margin-top: 10%;
  justify-content: center;
`
