import React, { useState } from 'react';
import styled from 'styled-components';
import DetailModal from './DetailModal';

interface Props {
    imageUrl: string;
    size: number;
}

export default function BookCover({ imageUrl, size }: Props) {
    const [modalOpen, setModalOpen] = useState<boolean>(false)

    const openModal = (): void => {
        setModalOpen(!modalOpen)
        console.log(modalOpen)
        window.scrollTo(0, 0)
    }

    const closeModal = (): void => {
        setModalOpen(false);
    }

    return (
        <>
            <DetailModal bookId={0} closeModal={closeModal} modalOpen={modalOpen}/>
            <BookImage size={size} imageUrl={imageUrl} onClick={openModal}/>
        </>
    );
}

const BookImage = styled.div<Props>`
  width: ${(props: Props) => `${props.size}px`};
  height: ${(props: Props) => `${props.size * 1.48}px`};
  background-image: url(${(props: Props) => props.imageUrl});
  background-size: contain;
  box-shadow: 2px 5px 4px 0px #00000040;
  margin: auto;
`;
