import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import ReactWordcloud from 'react-wordcloud';
import { getBestKeyword } from '../../apis/bestkeyword';

interface KeyWord {
  text: string;
  value: number;
}

export default function BestKeyword() {
  const [keywords, setKeywords] = useState<KeyWord[]>([])


  useEffect(() => {
    // 이달의 키워드 요청
    getKeyword()
  }, [])

  const getKeyword = async () => {
    // 현재 년, 월
    const date = new Date()
    const month = date.getMonth()
    const year = date.getFullYear()

    let tmp: KeyWord[] = [];
    const data = await getBestKeyword(String(year), String(month).padStart(2, '0'));

    data.forEach((item: any) => {
      tmp.push({
        text: item.children[0].value.slice(0, -2),
        value: Number(item.children[1].value.slice(0, -2)),
      })
    })
    console.log(tmp)

    setKeywords(tmp)
  }

  return (
    <Container>
      <Title>
        { }월의 키워드
        <br />
        TOP 10
      </Title>
      <Desc>"광주북구운암도서관"의 인기 도서를 만나보세요!</Desc>
      {/* <>
        {
          keywords.map((keyword: KeyWord, idx) => {
            return (
              <div key={idx}>
                {keyword.word}
                {keyword.weight}
              </div>
            )
          })
        }
      </> */}
      {/* <ReactWordcloud words={keywords}/> */}
      {/* <ReactWordcloud
      callbacks={callbacks}
      options={options}
      size={size} 
      keywords={keywords}/> */}
    </Container>
  );
}


// Styled Components
const Container = styled.div`
  width : 100%;
  height: 550px;
  background-color: #F7F3EB;
  display: flex;
  flex-direction: column;
  justify-content: center;
`

const Title = styled.div`
  font-size: 25px;
  font-weight: 700;
  text-align: center;
  margin: 3% 0; 
  text-shadow: 0px 3px 3px #00000040;
`;

const Desc = styled.div`
  margin-top: 7%;
  font-size: 13px;
  font-weight: 500;
  color: #5c5c5c;
  text-align: center;
`;
