import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import banner1 from "../../Assets/Images/banner1.png";
import banner2 from "../../Assets/Images/banner2.png";
import banner3 from '../../Assets/Images/banner3.png';
import banner4 from "../../Assets/Images/banner4.png";
import { ArrowRightCircleIcon } from "@heroicons/react/24/outline";
import useSelectorTyped from "../../Store";
// APIs
import { getContentsRecommendAPI } from "../../apis/recommend";
// Swiper
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import { Autoplay } from "swiper";

interface Book {
  imageUrl: string;
  title: string;
  category: string[];
  publisher: string;
  author: string;
}

export default function Banner() {
  const navigate = useNavigate();
  const token = window.localStorage.getItem("user");
  const userId = useSelectorTyped((state) => state.user.userid);

  const handleClickRecommend = () => {
    const books = getContentsRecommend();
    navigate("/list", { state: { books: books, type: 1 } });
  };

  const getContentsRecommend = async () => {
    const data = await getContentsRecommendAPI(userId);
    let tmp: Book[] = [];
    data.forEach((book: any) => {
      let test = book.category.replaceAll("'", '"');
      let category = JSON.parse(test);
      tmp.push({
        title: book.title,
        category: category,
        imageUrl: book.image_url,
        publisher: book.publisher,
        author: book.author,
      });
    });
    return tmp;
  };

  return (
    <Swiper
      spaceBetween={50}
      slidesPerView={1}
      autoplay={{
        delay: 5000,
        disableOnInteraction: false,
      }}
      modules={[Autoplay]}
      loop={true}
    >
      <SwiperSlide>
        <Slide1>
          <Link1 onClick={() => handleClickRecommend()}>
            <Text>내 취향 분석 도서 보러가기</Text>
            <ArrowRightCircleIcon width="20px" color="white" />
          </Link1>
        </Slide1>
      </SwiperSlide>
      <SwiperSlide>
        <Slide2>
          <Link2>
            <Text>광주 도서관의 인기 도서 보러가기</Text>
            <ArrowRightCircleIcon width="20px" color="white" />
          </Link2>
        </Slide2>
      </SwiperSlide>
        <SwiperSlide>
          <Slide4/>            
        </SwiperSlide>
    </Swiper>
  );
}

const Slide1 = styled.div`
  width: 100%;
  height: 80vh;
  background: url(${banner1});
  background-size: cover;
  background-position: center;
`;

const Link1 = styled.div`
  display: flex;
  align-items: center;
  position: absolute;
  bottom: 8%;
  width: 100%;
  justify-content: center;
`;

const Text = styled.div`
  color: white;
  font-size: 16px;
  margin-right: 5px;
`;

const Slide2 = styled.div`
  width: 100%;
  height: 80vh;
  background: url(${banner2});
  background-size: cover;
  background-position: center;
`;

const Link2 = styled.div`
  display: flex;
  align-items: center;
  position: absolute;
  bottom: 8%;
  width: 95%;
  justify-content: flex-end;
  margin-right: 5%;
`;



const Slide4 = styled.div`
  width: 100%;
  height: 80vh;
  background: url(${banner4});
  background-size: cover;
  background-position: center;
`;
