import React from "react";
import mypagebanner from "../../Assets/Images/mypagebanner.png";
import styled from "styled-components";
import useSelectorTyped from "../../Store";

export default function MypageBanner() {
  const nickname = useSelectorTyped((state) => state.user.name);
  return (
    <>
    <Burgur>
      <svg
        width="25"
        height="19"
        viewBox="0 0 25 19"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
        >
        <line y1="0.5" x2="25" y2="0.5" stroke="white" />
        <line y1="9.5" x2="25" y2="9.5" stroke="white" />
        <line y1="18.5" x2="25" y2="18.5" stroke="white" />
      </svg>

          </Burgur>
      <BannerImage>
        <UserNameText>{nickname}님의 책방</UserNameText>
        <IntroText>{nickname}님의 취향이 가득 찬 방입니다.</IntroText>
      </BannerImage>
    </>
  );
}

const Burgur= styled
const BannerImage = styled.div`
  background-blend-mode: multiply;
  background-color: rgba(0, 0, 0, 0.3);
  width: 100vw;
  height: 30vh;
  background-image: url(${mypagebanner});
  background-size: cover;
`;

const UserNameText = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  padding-top: 18%;
  font-size: 25px;
  font-weight: bold;
  color: white;
  font-family: "Gowun Batang", serif;
`;

const IntroText = styled.span`
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
  color: white;
  font-family: "Gowun Batang", serif;
  padding-top: 5%;
`;
