import React,{useState} from "react";
import styled from "styled-components";
import ImageAI from "../../Components/Contents/ImageAI";

interface Props {
  imageUrls: string[];
}

export default function Images({ imageUrls }: Props) {
  // 선택할 인덱스
  const [selectedIdx,setSelectedIdx] = useState<number>(0);
  //클릭 시 인덱스 업데이트
  const handleChange =(idx:number) => {
    setSelectedIdx(idx)
  }
  



  return (
  
    <Container>
        <Contents>
            <Minis>
            {/* for문 돌려 */}
            {imageUrls?.map((image,idx) => (
              <div onClick = {()=> handleChange(idx)}>

                <ImageAI key = {idx} imageUrl={image} size = '100px' />
              </div>

            ))}
            </Minis>
            {/* 선택된 사진 업데이트 */}
            <Selected>
              <ImageAI imageUrl={imageUrls[selectedIdx]} size="200px"/>
            </Selected>
        </Contents>
      <ButtonContainer>
        <Button type="submit">확인</Button>
      </ButtonContainer>
    </Container>
  );
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 90%;
  margin: auto;
`;

const Contents = styled.div`
    width : 100%;
    height : 50%;
    border : 2px solid red;
    display: flex;
`
const Minis = styled.div`
    border : 2px solid red;
    
`
const Selected = styled.div`
    border : 2px solid red;
    
`

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 1.5rem;
  width: 100%;
`;

const Button = styled.button`
  border: none;
  box-shadow: rgba(97, 88, 88, 0.25) 0px 4px 1px;
  background-color: #dbd4c3;
  height: 50px;
  width: 100%;
  color: #5c5649;
  font-weight: bold;
`;
