import axios from 'axios';


const baseUrl = process.env.REACT_APP_API_URL;
const token = localStorage.getItem('user');
export async function writeTextAPI(text: string, bookId:number) {
    try{
        const {data} = await axios ({
            method:'post',
            url:'/book/review',
            data: {text,bookId},
            headers: {
                Authorization: token,
            }
        })

    return data

    }catch{

    }
}
