import React, { useEffect, useState } from "react";
import styled from "styled-components";
import sample from '../../Assets/Images/sample.png'

// import Components

// Swiper
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/effect-coverflow";
import { EffectCoverflow } from "swiper";
import BookCover from "../../Components/Contents/BookCover";
import { getLibraryBooksAPI } from "../../apis/region";

// Type
type Props = {
  // 도서관 추천 리스트
};

// Component
export default function LibraryBooks() {
  const [books, setBooks] = useState();

  useEffect(() => {
    // 현재 지역 도서관 인기도서 불러오기
    getLibraryBooks()
  }, [])

  const getLibraryBooks = async () => {
    const data = await getLibraryBooksAPI()
    console.log(data)
    
    let tmp = []
    

  }

  return (
    <Container>
      <Title>
        가장 가까운 도서관의
        <br />
        인기 대출 도서
      </Title>
      <Desc>"광주북구운암도서관"의 인기 도서를 만나보세요!</Desc>
      <SwiperWrap>
        <Swiper
          effect={"coverflow"}
          grabCursor={true}
          centeredSlides={true}
          slidesPerView={2}
          coverflowEffect={{
            rotate: 50,
            stretch: 0,
            depth: 100,
            modifier: 1,
          }}
          modules={[EffectCoverflow]}
        >
          <SwiperSlide>
            <BookCover imageUrl={sample} size={170} />
          </SwiperSlide>
          <SwiperSlide>
            <BookCover imageUrl={sample} size={170} />
          </SwiperSlide>
          <SwiperSlide>
            <BookCover imageUrl={sample} size={170} />
          </SwiperSlide>
        </Swiper>
      </SwiperWrap>
    </Container>
  );
};


// Styled Components
const Container = styled.div`
  width : 100%;
  height: 550px;
  background-color: #F9F9F7;
  display: flex;
  flex-direction: column;
  justify-content: center;
`

const Title = styled.div`
  font-size: 25px;
  font-weight: 700;
  text-align: center;
  margin: 3% 0; 
  text-shadow: 0px 3px 3px #00000040;
`;

const Desc = styled.div`
  margin-top: 7%;
  font-size: 13px;
  font-weight: 500;
  color: #5c5c5c;
  text-align: center;
`;

const SwiperWrap = styled.div`
  margin-top: 15%;
`
