import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import ReactWordcloud from 'react-wordcloud';
import { getBestKeyword } from '../../apis/bestkeyword';

interface KeyWord {
  text: string;
  value: number;
}

export default function BestKeyword() {
  const [keywords, setKeywords] = useState<KeyWord[]>([])

  useEffect(() => {
    // 이달의 키워드 요청
    getKeyword()
  }, [])

  const options: any = {
    colors: ["#edb02b", "#3464f7","#258247","#ed2b82","#57544d","#6830bd",],
    enableTooltip: true,
    deterministic: false,
    fontFamily: "Inter",
    fontSizes: [15, 65],
    fontStyle: "normal",
    fontWeight: "normal",
    padding: 1,
    rotations: 3,
    rotationAngles: [0, 90],
    scale: "sqrt",
    spiral: "archimedean",
    transitionDuration: 1000
  };

  const getKeyword = async () => {
    // 현재 년, 월
    const date = new Date()
    const month = date.getMonth()
    const year = date.getFullYear()

    let tmp: KeyWord[] = [];
    const data = await getBestKeyword(String(year), String(month).padStart(2, '0'));

    data.forEach((item: any) => {
      tmp.push({
        text: item.children[0].value.slice(0, -2),
        value: Number(item.children[1].value.slice(0, -2)),
      })
    })

    setKeywords(tmp)
  }

  return (
    <Container>
      <Title>
        지난 달의 키워드
        <br />
        TOP 70
      </Title>
      <Desc>"광주북구운암도서관"의 인기 도서를 만나보세요!</Desc>
      {/* <>
        {
          keywords.map((keyword: KeyWord, idx) => {
            return (
              <div key={idx}>
                {keyword.word}
                {keyword.weight}
              </div>
            )
          })
        }
      </> */}
      <WordCloud>
        <ReactWordcloud words={keywords} options={options} />
        {/* <ReactWordcloud
      callbacks={callbacks}
      options={options}
      size={size} 
    keywords={keywords}/> */}
      </WordCloud>
    </Container>
  );
}


// Styled Components
const Container = styled.div`
  width : 100%;
  height: 550px;
  background-color: #F7F3EB;
  display: flex;
  flex-direction: column;
  justify-content: center;
`

const Title = styled.div`
  font-size: 25px;
  font-weight: 700;
  text-align: center;
  margin: 3% 0; 
  text-shadow: 0px 3px 3px #00000040;
`;

const Desc = styled.div`
  margin-top: 5%;
  font-size: 13px;
  font-weight: 500;
  color: #5c5c5c;
  text-align: center;
`;

const WordCloud = styled.div`
  width : 90%;
  margin: 6% auto;
  border: 2px solid red;
  height: 50%;
`
