import React, { useEffect, useState } from "react";
import styled from "styled-components";
import sample from "../../Assets/Images/sample.png";
import Book from "../../Components/Contents/Book";
import BookCover from "../../Components/Contents/BookCover";
import { PlusCircleIcon } from '@heroicons/react/24/outline'

// 컴포넌트 정의
export default function ReadingBooks() {

  return (
    <Container>
      <Title>나혜승님이 읽고 있는 책</Title>
      <List>
        <AddBtn><PlusCircleIcon width="50px" strokeWidth="1px" color="#5c5649"/></AddBtn>
        <BookCover imageUrl={sample} size={120} />
        <BookCover imageUrl={sample} size={120} />
      </List>
    </Container>
  );
}

// Styled Components
const Container = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;
    margin: 5% auto 10% auto;
  `
const Title = styled.div`
    font-size: 16px;
    font-weight: bold;
    color: #5c5649;
    width: 90%;
    margin: 5% auto;
  `;

const List = styled.div`
    width: 90%;
    margin: auto;
    display: flex;
    align-items: center;
    overflow-x: scroll;
    &::-webkit-scrollbar {
      display: none;
    }
  `;

const AddBtn = styled.div`
    /* margin: 8%; */
    width: 120px;
    height: 180px;
    
  `
