import React, { useEffect } from 'react';
import styled from 'styled-components';
import useSelectorTyped from '../../Store';
import sample from '../../Assets/Images/sample.png'
import { getContentsRecommendAPI } from '../../apis/recommend';

export default function Recommend() {
    const nickname = useSelectorTyped((state) => state.user.name)
    const userId = use

    useEffect(() => {
        getConentsRecommend()
    }, [])

    const getConentsRecommend = async () => {
        const data = await getContentsRecommendAPI();
        console.log(data)
    }

    return (
        <Container>
            <Title>{nickname}님을 위한<br></br>추천도서</Title>
            <Tags>#취향분석 #맞춤추천</Tags>
            <Contents>
                <LinkTag>
                    <Book>
                        <Top></Top>
                        <Side></Side>
                        <Front></Front>
                    </Book>
                </LinkTag>
            </Contents>
        </Container>
    );
}

const Container = styled.div`
    width : 100%;
  height: 600px;
    background-color: #E3E3CF;
    display: flex;
    flex-direction: column;
    align-items: center;
`

const Title = styled.div`
    font-family: 'Gowun Dodum', sans-serif;
    font-weight: 800;
    font-size: 25px;
    text-align: center;
    margin : 12% auto 5% auto;
`

const Tags = styled.div`
    font-family: 'Nanum Gothic Coding', monospace;
    font-weight: 500;
    font-size: 15px;
    margin-bottom: 3%;
`

const Contents = styled.div`
    width : 90%;
    height: 60%;
    margin : 10% 0 5% 0;
    border-radius: 200px 200px 0 0;
    background-color: white;
`
const LinkTag = styled.div`
    div {
    position: absolute;
    box-sizing: border-box;
    transform-origin: left top;
    }
`

const Book = styled.div`
border: 2px solid black;
   position: relative;
  /* width: 100%; */
  /* height: 100%; */
  transform-style: preserve-3d;
  transform: rotateX(20deg) rotateY(20deg);
  > div {
    position: absolute;
  }

`
const Top = styled.div`
    transform: rotateX(90deg) translateZ(20px) translateX(16px) translateY(-50px);
    width: 180px;
    height: 50px;
    background-color: blue;
`

const Side = styled.div`
    width: 50px;
    height: 270px;
    background-color: red;
   transform: rotateY(90deg) translateZ(380px) translateX(500px) translateY(-215px);
`
const Front = styled.div`
width : 180px;
height : 270px;
background-size: cover;
   /* transform: translateZ(50px); */
   background-image: url(${sample});
`
