import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { PaperAirplaneIcon } from "@heroicons/react/24/outline";
import { writeTextAPI } from "../../apis/write";
import { useParams } from "react-router-dom";
import { getWriteAPI } from "../../apis/write";
import Comment from "./Comment";

// 독후감등록 타입 시정
interface Comment {
  bookId: number;
  content: string;
}

// 독후감 리스트 타입 지정
interface CommentItem {
  contentId: number;
  content: string;
}

export default function TextForm() {
  const bookId: number = Number(useParams().bookid);
  const [content, setContent] = useState<string>("");

  //둑후감은 Comment라는 타입을 사용한다는 뜻,
  const comment: Comment = {
    bookId: bookId,
    content: content,
  };

  // bookId와 text를 한꺼번에 보내기
  const handleTextChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setContent(event.target.value);
  };

  const [odd, setOdd] = useState<CommentItem[]>([]);
  const [even, setEven] = useState<CommentItem[]>([]);

  const writeText = async () => {
    if (content !== "") {
      await writeTextAPI(comment);
      // 받은 데이터가 리스트일 때 foreach를 써서 넣어주기
      setContent("");
      alert("등록되었습니다. ");
      getWrite();
    } else {
      alert("글을 입력해주세요");
    }
  };

  // 독후감 리스트 불러오는 aip 호출
  const getWrite = async () => {
    const data = await getWriteAPI();
    let oddlist: CommentItem[] = [];
    let evenlist: CommentItem[] = [];
    console.log(data);

    data.forEach((item: CommentItem) => {
      console.log(item.contentId, "🎄");
      if (item.contentId % 2 === 1) {
        oddlist.push({
          contentId: item.contentId,
          content: item.content,
        });
      } else {
        evenlist.push({
          contentId: item.contentId,
          content: item.content,
        });
      }
    });
    setOdd(oddlist);
    setEven(evenlist);
  };

  useEffect(() => {
    getWrite();
  }, []);

  return (
    <>
      <Container>
        <Title>여러분의 생각을 남겨보세요.</Title>
        <InputBox>
          <Input
            type="text"
            value={content}
            // placeholder="여기에 글을 작성해주세요."
            onChange={handleTextChange}
          />
          <Button onClick={writeText}>
            <Icon>
              <PaperAirplaneIcon />
            </Icon>
          </Button>
        </InputBox>
        <ContentContainer>
          <FirstBox>
            {odd?.map((content: CommentItem) => {
              return (
                <Comment key={content.contentId} content={content.content} />
              );
            })}
          </FirstBox>

          <SecondBox>
            {even?.map((content: CommentItem) => {
              return (
                <Comment key={content.contentId} content={content.content} />
              );
            })}
          </SecondBox>
        </ContentContainer>
      </Container>
    </>
  );
}

const Container = styled.div`
  height: 100;
  width: 90%;
  margin: auto;
`;

const InputBox = styled.div`
  display: flex;
  align-items: center;
  padding-right: 8px;
`;

const Title = styled.div`
  color: #5c5649;
  font-weight: bold;
  margin-top: 0.3rem;
`;

const Input = styled.input`
  background-color: transparent;
  outline: none;
  border-top: none;
  border-right: none;
  border-left: none;
  border-bottom: 1px solid #5c5649;
  width: 100%;
  height: 2.3rem;
  margin-top: 0.4rem;
`;

const Button = styled.button`
  border: none;
  background-color: transparent;
`;

const Icon = styled.div`
  border: #5c5649;
  height: 20px;
  width: 20px;
`;

const ContentContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
`;

const FirstBox = styled.div`
  display: flex;
  flex-direction: column;
`;
const SecondBox = styled.div`
  display: flex;
  flex-direction: column;
`;
