import React, { useEffect } from 'react';
import styled from 'styled-components';
import useSelectorTyped from '../../Store';
import sample from '../../Assets/Images/sample.png'
import bookside from '../../Assets/Images/bookside.png';
import booktop from '../../Assets/Images/booktop.png';
import { getContentsRecommendAPI } from '../../apis/recommend';

// swiper
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import { Pagination } from "swiper";

export default function Recommend() {
    const nickname = useSelectorTyped((state) => state.user.name)
    const userId = useSelectorTyped((state) => state.user.userid)

    useEffect(() => {
        getConentsRecommend()
    }, [])

    const getConentsRecommend = async () => {
        const data = await getContentsRecommendAPI(userId);

    }

    return (
        <Container>
            <Title>{nickname}님을 위한<br></br>추천도서</Title>
            <Tags>#취향분석 #맞춤추천</Tags>
            <Contents>
                <SwiperWrap>
                    <Swiper style={{ height: '100%' }}
                        direction={"vertical"}
                        modules={[Pagination]}
                        className="mySwiper"
                    >
                        <SwiperSlide style={{width:'80%'}}>
                            <BookInfo>
                            <LinkTag>
                            <Book>
                                <Top></Top>
                                <Side></Side>
                                <Front></Front>
                            </Book>
                        </LinkTag>
                            </BookInfo>
                        </SwiperSlide>
                        <SwiperSlide>Slide 2</SwiperSlide>
                        <SwiperSlide>Slide 3</SwiperSlide>
                    </Swiper>
                </SwiperWrap>

            </Contents>
        </Container>
    );
}

const Container = styled.div`
    width : 100%;
    height: 600px;
    background-color: #E3E3CF;
    display: flex;
    flex-direction: column;
    align-items: center;
`

const Title = styled.div`
    font-family: 'Gowun Dodum', sans-serif;
    font-weight: 800;
    font-size: 25px;
    text-align: center;
    margin : 12% auto 5% auto;
`

const Tags = styled.div`
    font-family: 'Nanum Gothic Coding', monospace;
    font-weight: 500;
    font-size: 15px;
    margin-bottom: 3%;
`

const Contents = styled.div`
    width : 90%;
    height: 65%;
    margin : 7% 0 5% 0;
    border-radius: 200px 200px 0 0;
    background-color: white;
    display: flex;
    justify-content: center;
    align-items: center;
`

const SwiperWrap = styled.div`
    width: 90%;
    height: 100%;
    border: 2px solid red;
`

const BookInfo = styled.div`
    margin: auto;
    border : 2px solid blue;
    display: flex;
    flex-direction: column;
    align-items: center;
`

const LinkTag = styled.div`
    div {
    position: absolute;
    box-sizing: border-box;
    transform-origin: left top;
    }
`

const Book = styled.div`
    margin: 10% 0 0 0;
   transform-style: preserve-3d;
   transform: rotateX(20deg) rotateY(20deg);
   > div {
    position: absolute;
   }
   width : 180px;
   height: 270px;
`
const Top = styled.div`
    transform: rotateX(90deg) translateZ(20px) translateX(20px) translateY(-48px);
    width: 180px;
    height: 50px;
    background-image: url(${booktop});
    background-size: cover;
`

const Side = styled.div`
    width: 50px;
    height: 270px;
    background-image: url(${bookside});
    background-size: cover;
    transform: rotateY(90deg) translateZ(350px) translateX(500px) translateY(-207px);
`
const Front = styled.div`
    width : 180px;
    height : 270px;
    background-size: cover;
   background-image: url(${sample});

`
