import { useNavigate } from "react-router-dom";
import axios from "axios";
import { ListFormat } from "typescript";


export default function Home() {


  

  return (
    <>
      <button
        onClick={() => {
          navigate("/reader");
        }}
      >
        책 추가하기
      </button>
      <h3>베스트셀러 Top20</h3>
      <div>
        {bestBooks.map((book) => (
          <div key={book.id}>
            <img src={book.image_url} height="160" width="100" />
            {book.title}
          </div>
        ))}
      </div>

      <h3>유사한 책 추천</h3>
      <div>
        {similarBooks.map((book) => (
          <div key={book.id}>
            <img src={book.image_url} height="160" width="100" />
            {book.title}
          </div>
        ))}
      </div>
    </>
  );
}
