import React, { useEffect, useState } from 'react';
import { useLocation, useParams } from 'react-router';
import NavBack from '../../Components/Contents/NavBack';
import LibraryList from './LibraryList';

export default function Library() {
   const param = useParams();
   const location = useLocation();
   let text = ''
   if(location.state.title.length > 12) text = location.state.title.slice(0, 12)+ ' 도서관 정보'
    else text = location.state.title + ' 도서관 정보'

    return (
        <>
            <NavBack text="`${location.state.title}` 도서관 정보" link="-1" />
            <LibraryList/>
        </>
    );
}
