import React from 'react';
import styled from 'styled-components';
import intro from '../../Assets/Images/intro.png'

export default function Intro() {
    return (
        <Container>
            <BackGround src={intro}></BackGround>
            <Contents>
                <Title>북,돋움이 추천하는<br />당신만을 위한 도서는?</Title>
                <Login><div>로그인</div></Login>
                <SignUp>북,돋움이 처음이세요? <span style={{ textDecoration: 'underline' }}>회원가입</span></SignUp>
            </Contents>
        </Container>
    );
}

const Container = styled.div`
width: 100%;
height: fit-content;
border: 2px solid red;
`

const BackGround = styled.img`
width: 100%;
max-width: 35rem;
border: 2px solid red;
display: block;
position: absolute;
z-index: -1;
`

const Contents = styled.div`
    border: 2px solid red;
    max-width: 33rem;
    width:80%;
    position: absolute;
    bottom: 10%;
    left: 50%;
    transform: translate(-50%, 0%);
`

const Title = styled.div`
margin-bottom : 20%;
    color: #F7F3EB;
    font-size: 1.4rem;
    font-family: 'Gowun Batang', serif;
`

const Login = styled.div`
    background-color: #F7F3EB;
    width: 100%;
    height: 50px;
    border-radius: 5px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #5C5649;
    font-weight: bold;
    font-size: 1.2rem;
    margin-bottom : 5%;
`

const SignUp = styled.div`
    color: #F7F3EB;
    width: 100%;
    text-align: center;
`
