import React from 'react';
import styled from 'styled-components';
import logo from '../../Assets/Images/logo-white.png';

export default function Survey() {
    return (
        <Container>
            <Logo src={logo}></Logo>
            <Question>당신이 책을 읽는 이유는 무엇인가요?</Question>
            <BarContainer>
                <ul>
                    <li>
                        <ProgressBar />
                    </li>
                </ul>
            </BarContainer>
            <Contents></Contents>
        </Container>
    );
}

const Container = styled.div`
    background: #5C5649;
    width: 100%;
    height: 100vh;
    position: relative;
`

const Logo = styled.img`
    width: 13%;
    position: absolute;
    top: 2%;
    left: 4%;
    display: block;
`
const Question = styled.div`
position: absolute;
left: 50%;
top : 8rem;
text-align: center;
color: white;
font-family: 'Gowun Batang', serif;
transform: translate(-50%, 0%);
`

const BarContainer = styled.div`
    position: relative;
      height: 100%;
      flex: 0 0 30%;
      margin: 0 auto;
      display: flex;
      align-items: center;
      justify-content: center;
      ul {
    position: relative;
    padding: 0;
    list-style: none;
    width: 150px;
    li {
      background-color: #fff;
      height:20px; 
      border-radius:10px;
    }
  }
`

const ProgressBar = styled.div`
    width: 80%; 
    -moz-animation: css-progressbar 2s ease-out;
    -webkit-animation: css-progressbar 2s ease-out;
    position: absolute;
        border-radius: 10px; 
        background-color: white;
        opacity: 70%;
        height: 20px;
        &:after  {
          -webkit-box-sizing: border-box;
          box-sizing: border-box;
          content: "";
          width: 27px;
          height: 27px;
          position: absolute;
          top: -3px;
          right: 0;
          border-radius: 50%;
          background-color:white;
        }
`
