import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import useSelectorTyped from '../../Store';
// Components
import Nav from '../../Components/Common/Nav';
import Book from '../../Components/Contents/Book'
// Types
import { BookInfo } from '../../Store/Types';
import { useLocation } from 'react-router';


export default function RecommendList() {
  const location = useLocation()
  console.log(location.state)
  const nickname = useSelectorTyped((state) => state.user.name);
  const type = 1

  return (
    <>
      <Nav />
      <Contents>
        { type === 1 ? 
        <Title>"{}을 읽은 사람이 선택한 도서"</Title>
        : <Title>"{nickname} 님이 관심있는 분야의 도서"</Title>
      }
        <BooksWrap>
          {/* <>
            {
              books.map((book) => {
                return (
                  <Book key={book.bookId} book={{
                    imageUrl: book.imageUrl,
                    title: book.title,
                    category: book.category,
                    publisher: book.publisher,
                    isbn: 0,
                  }} />
                  )
              })
            }
          </> */}
        </BooksWrap>
      </Contents>
    </>
  );
}

const Contents = styled.div`
  width: 90%;
  margin: auto;
  display: flex;
  flex-direction: column;
  padding-top:5%                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ;
`

const Title = styled.div`
  font-weight: 600;
  font-size: 16px;
  color: #5c5649;
  text-align: center;
  margin: 2% 0 3% 0;
`

const BooksWrap = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  padding-top: 4%;
  width: 95%;
  margin:auto;
  justify-content: center;
`
