import React, { useState, useEffect } from "react";
import { InView, useInView } from "react-intersection-observer";
import { useLocation, useParams } from "react-router-dom";
import profile from "../../Assets/Images/profile.png";
import styled from "styled-components";
// Components
import NavBack from "../../Components/Contents/NavBack";
import ListCard from "./ListCard";
// APIs
import {
  getMeetingCommentAPI,
  postMeetingCommentAPI,
  postMeetingJoinAPI,
  getCommentAuthorityAPI,
} from "../../apis/meeting";
import { CommentInfo } from '../../Store/Types'

interface Comment {
  meetingId: number;
  content: string;
}

export default function List() {
  const id: number = Number(useParams().meetid);

  const [idx, setIdx] = useState<number>(0);
  const [ref, inView] = useInView();

  const [comments, setComments] = useState<CommentInfo[]>([]);
  const location = useLocation();
  const title = location?.state?.title;

  // Î™®ÏûÑÏßÄÍ∏∞Ïùò Îßê
  const master = location?.state?.userName;
  const masterContent = location?.state?.content;

  // ÎåìÍ∏Ä ÏûÖÎ†•
  const [text, setText] = useState<string>("");

  // ÎåìÍ∏Ä Í∂åÌïú
  const [authority, setAuthority] = useState<boolean>(false);

  // ÎåìÍ∏Ä Ïò¨Î¶¥ Îïå ÌïÑÏöîÌïú Ï†ïÎ≥¥
  const comment: Comment = {
    meetingId: id,
    content: text,
  };

  // useEffect(() => {
  //   getMeetingComment(idx);
  // }, [idx]);

  // Î¨¥Ìïú Ïä§ÌÅ¨Î°§
  useEffect(() => {
    if (InView) {
      console.log('üòÄ')
      getMeetingComment(comments[comments.length - 1]?.commentId)
      setIdx(comments[comments.length - 1]?.commentId);
    }
  }, [inView]);

  // Î™®ÏûÑ ÎåìÍ∏Ä axios Î∂àÎü¨Ïò§Í∏∞
  const getMeetingComment = async (idx: number) => {
    const data = await getMeetingCommentAPI(id, idx);
    let list: CommentInfo[] = [];

    data.forEach((item: CommentInfo) => {
      list.push({
        commentId: item.commentId,
        userId: item.userId,
        userName: item.userName,
        leader_content: item.leader_content,
        content: item.content,
      });
    });
    setComments([...comments, ...list]);
  };

  // ÎåìÍ∏Ä ÏûëÏÑ±ÌïòÎäî axios
  const postMeetingComment = async () => {
    await postMeetingCommentAPI(comment);
    alert("ÎåìÍ∏ÄÏù¥ Îì±Î°ùÎêòÏóàÏäµÎãàÎã§.");
    setText("");
    postMeetingJoin();
    getMeetingComment(idx); // Îì±Î°ùÌïú ÌõÑ axios Îã§Ïãú Ìò∏Ï∂ú
  };

  // enterÎ°ú ÎåìÍ∏Ä Îì±Î°ùÌïòÍ∏∞
  const handleKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === "Enter") {
      if (text.trim() !== "") {
        postMeetingComment();
      } else {
        alert("ÎåìÍ∏ÄÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      }
    }
  };

  const getCommentAuthority = async () => {
    const data = await getCommentAuthorityAPI(id);
    setAuthority(data);
  };

  useEffect(() => {
    getCommentAuthority();
  }, []);

  // Î™®ÏûÑ Ï∞∏Ïó¨ÌïòÍ∏∞
  const postMeetingJoin = async () => {
    await postMeetingJoinAPI(id);
  };

  return (
    <Container>
      <NavBack text={title} link={"-1"} />

      <Comment>
        {/* Î™®ÏûÑÏßÄÍ∏∞Ïùò Îßê */}
        <Master style={{ backgroundColor: "#E7E1D2" }}>
          <ProfileImg src={profile} />
          <TopDiv>
            <Name>{master}</Name>
            <Owner>Î™®ÏûÑÏßÄÍ∏∞Ïùò Îßê</Owner>
          </TopDiv>
          <Text>{masterContent}</Text>
        </Master>

        {/* Î™®ÏûÑ ÎåìÍ∏Ä */}
        <Wrapper>
          {comments?.map((info: CommentInfo) => (
            <ListCard {...info} key={info.commentId} />
          ))}
          <Ref ref={ref} style={{ height: authority ? "61px" : "0px" }} />
        </Wrapper>
      </Comment>

      {/* ÎåìÍ∏Ä ÏûÖÎ†• */}
      {authority ? (
        <Input
          type="text"
          placeholder="ÏÉùÍ∞ÅÏùÑ ÎÇòÎà† Ï£ºÏÑ∏Ïöî"
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyPress={handleKeyPress}
        />
      ) : null}
    </Container>
  );
}

const Container = styled.div`
  background-color: #f9f9f7;
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
`;

const Comment = styled.div`
  height: 95%;
  overflow-y: scroll;
  display: flex;
  flex-direction: column;
`;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const Master = styled.div``;

const ProfileImg = styled.img`
  border-radius: 100px;
  width: 45px;
  height: 45px;
  float: left;
  margin: 5%;
  display: flex;
`;

const TopDiv = styled.div`
  display: flex;
  align-items: center;
  padding-top: 3%;
`;

const Name = styled.div`
  color: #9b9b9b;
  font-weight: 600;
`;

const Owner = styled.div`
  color: #9b9b9b;
  font-size: 0.8rem;
  margin: 2%;
`;

const Text = styled.div`
  font-size: 0.8rem;
  display: flex;
  padding: 0 5% 3% 0;
`;

const Input = styled.input`
  box-sizing: border-box;
  background-color: #f9f9f7;
  border: 8px solid #f7f3eb;
  bottom: 0;
  position: fixed;
  width: 100%;
  height: 60px;
  padding: 3%;
  ::placeholder,
  ::-webkit-input-placeholder {
    color: #c9c9c9;
  }
`;

const Ref = styled.div`
  width: 100%;
`;
