    const nickname = useSelectorTyped((state) => state.user.name)
    return (
        <Container>
            <Title>{nickname}님 만을 위한<br></br>추천도서</Title>
            <Tags>#취향분석 #맞춤추천</Tags>
            
        </Container>
    );
}

const Container = styled.div`
    width : 100%;
    background-color: #FCFCEE;
`

