import React from 'react';
import styled from 'styled-components';

export default function BestKeyword() {
    return (
        <Container>
            <Title>
                이번 달 인기 키워드
                <br />
                인기 대출 도서
            </Title>
            <Desc>"광주북구운암도서관"의 인기 도서를 만나보세요!</Desc>
        </Container>
    );
}


// Styled Components
const Container = styled.div`
  width : 100%;
  height: 550px;
  background-color: #F7F3EB;
  display: flex;
  flex-direction: column;
  justify-content: center;
`

const Title = styled.div`
  font-size: 25px;
  font-weight: 700;
  text-align: center;
  margin: 3% 0; 
  text-shadow: 0px 3px 3px #00000040;
`;

const Desc = styled.div`
  margin-top: 7%;
  font-size: 13px;
  font-weight: 500;
  color: #5c5c5c;
  text-align: center;
`;
