/* global kakao */
import React, { useEffect, useState } from "react";
import Nav from "../../Components/Common/Nav";
import DetailModal from "../../Components/Contents/DetailModal";
import Banner from "./Banner";
import BookList from "./BookList";
import LibraryBooks from "./LibraryBooks"
import sample from '../../Assets/Images/sample.png'
import ReadingBooks from "./ReadingBooks";
import BestKeyword from "./BestKeyword";
import { useQuery } from "@tanstack/react-query";

declare global {
  interface Window {
    kakao: any;
  }
}

const { kakao } = window;

export default function Home() {
  const [map, setMap] = useState(null)
  const [curLocation, setCurLocation] = useState<[number, number]>([33.457701, 126.570667])

  useEffect(() => {
    // 현재 위치 정보 - 좌표
    navigator.geolocation.getCurrentPosition((position) => {
     
      })
      const container = document.getElementById('map')
      const options = {
        center: new kakao.maps.Lating(curLocation),
          level: 3
        }
      let map = new kakao.maps.Map(container, options)
      var geocoder = new kakao.maps.services.Geocoder();
      geocoder.addressSearch(meeting_place, function(result, status) {

          // 정상적으로 검색이 완료됐으면 
           if (status === kakao.maps.services.Status.OK) {
      
              var coords = new kakao.maps.LatLng(result[0].y, result[0].x);
      
              // 결과값으로 받은 위치를 마커로 표시합니다
              var marker = new kakao.maps.Marker({
                  map: map,
                  position: coords
              });
      
              // 인포윈도우로 장소에 대한 설명을 표시합니다
              var infowindow = new kakao.maps.InfoWindow({
                  content: '<div style="width:150px;text-align:center;padding:6px 0;">'+meeting_place+'</div>'
              });
              infowindow.open(map, marker);
      
              // 지도의 중심을 결과값으로 받은 위치로 이동시킵니다
              map.setCenter(coords);
          } 
      });    
      //위도, 경도로 변환 및 마커표시
      // let markerPosition  = new kakao.maps.LatLng(lat, lon); 
      // let marker = new kakao.maps.Marker({
      //     position: markerPosition
      // });
      // marker.setMap(map);

  }, [meeting_place]);

  return (
    <>
      <div id="map" style={{ width: "100%", height: "350px" }}></div>
      <Nav />
      <Banner />
      <ReadingBooks />
      <BookList />
      <LibraryBooks />
      {/* <DetailModal bookId={1}/> */}
      <BestKeyword />
    </>
  );
}
