import React from 'react';
import styled from 'styled-components';

export default function Result() {
    return (
        <Container>
        <Title>{nickname}님을 위한<br></br>추천도서</Title>
        <Tags>#취향분석 #맞춤추천</Tags>
        <Contents onClick={() => navigate('/list', { state: { books: books, type: 1 } })}>
            <SwiperWrap>
                <Swiper style={{ height: '100%' }}
                    direction={"vertical"}
                    pagination={{
                        clickable: true,
                    }}
                    modules={[Pagination]}
                    className="mySwiper"
                >
                    <>
                        {
                            books?.map((book) => {
                                return (
                                    <SwiperSlide key={book.title}>
                                        <BookInfo>
                                            <BookShape>
                                                <Top></Top>
                                                <Side></Side>
                                                <Front imageUrl={book.imageUrl}></Front>
                                            </BookShape>
                                            <Info>
                                                <BookTitle>{book.title.length > 10 ? book.title.slice(0, 10) + '...' : book.title}</BookTitle>
                                                <Categories>
                                                    <>
                                                        {
                                                            book?.category.map((item, idx) => <Category key={idx}>{item}</Category>)
                                                        }
                                                    </>
                                                </Categories>
                                                <InfoBottom>
                                                    <Author>글: {book.author}</Author>
                                                    <Publisher>출판사: {book.publisher}</Publisher>
                                                </InfoBottom>
                                            </Info>
                                        </BookInfo>
                                    </SwiperSlide>
                                )
                            })
                        }
                    </>

                </Swiper>
            </SwiperWrap>
        </Contents>
    </Container>
    );
}

const Container = styled.div`
    position: fixed;
    z-index: 5;
    width : 100%;
    height : 100vh;
    background-color: #DCD4C3;
  max-width: 35rem;

`
