import React, { useState } from "react";
import BookBanner from "../../Components/Contents/BookBanner";
import UserList from "../../Components/Contents/UserList";
import styled from "styled-components";
import MeetingList from "../../Components/Contents/MeetingList";
import { ArrowRightCircleIcon } from "@heroicons/react/24/outline";
import { PencilIcon } from "@heroicons/react/24/outline";
import { useNavigate, useLocation } from "react-router-dom";
import NavBack from "../../Components/Contents/NavBack";


export default function Mybook() {
  const navigate = useNavigate();
  const location = useLocation();
  const image = location?.state?.image;
  const writeText = () => {
    navigate("/write");
  };

  const [disable, setDisable] = useState<boolean>(false);
  const handleChnage = () => setDisable(true);

  return (
    <Container>
      <NavBack text="tit" link="/list"/>
      <BookBanner imageUrl={image}/>
      <UserList />
      <MeetingList />
      <Writng>
        <WriteIcon>
          <PencilIcon />
        </WriteIcon>
        <WritingText onClick={writeText}>독후감 확인하기</WritingText>
      </Writng>
      <Recommend>
        <RecommendText> 이 책을 읽은 다른사람이 선택한 책</RecommendText>
        <Icon>
          <ArrowRightCircleIcon />
        </Icon>
      </Recommend>

      <Button disabled={disable} onClick={handleChnage}>
        다 읽었어요!
      </Button>
    </Container>
  );
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
`;

const RecommendText = styled.div`
  margin: 10% 3% 10% 5%;
  color: #4a6eec;
`;
const Recommend = styled.div`
  display: flex;
  flex-direction: row;
`;

const Icon = styled.div`
  width: 25px;
  color: #4a6eec;
  margin-top: 10%;
`;
const Button = styled.button`
  width: 300px;
  height: 50px;
  color: #5c5649;
  background-color: #dbd4c3;
  margin: auto;
  border-color: transparent;

  &:disabled {
    opacity: 0.5;
  }
`;

const Writng = styled.div`
  display: flex;
  color: #5c5649;
  margin: 5%;
`;
const WriteIcon = styled.div`
  width: 15px;
  height: 15px;
`;
const WritingText = styled.div`
  color: #5c5649;
`;
