import React, { useEffect } from 'react';
import styled from 'styled-components';
import bookBanner from '../../Assets/Images/bookBanner.png'
import BookCover from './BookCover';
import sample from '../../Assets/Images/sample.png'
import { useParams } from 'react-router';
import { getmyBookAPI } from '../../apis/myBook';


export default function BookBanner() {
    const { bookid }: { bookid?: string } = useParams();

   useEffect (() => {
    if (bookid) {
    const getmyBook = async () => {
        const data = await getmyBookAPI(bookid)
    }
}
    getmyBook()
   },[bookid])

  
    return (
        <>
        <BannerImage>
            <BookCover name={'bannerimg'} imageUrl={sample} size={120}/>
        </BannerImage>
        </>
    );
}
const BannerImage = styled.div`
    width: 100vw;
    height: 30vh;
    background: url(${bookBanner});
    background-size: cover;
`
